document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".embla__viewport").forEach(e=>{const o=EmblaCarousel(e,{loop:!0},[EmblaCarouselAutoScroll({delay:1e3,speed:.5})]);var e=e.closest(".embla"),l=e.querySelector(".embla__button--prev"),t=e.querySelector(".embla__button--next");const r=e.querySelector(".embla__dots"),c=()=>{if(r){var e=r.querySelectorAll(".embla__dot");const t=o.selectedScrollSnap();e.forEach((e,l)=>{e.classList.toggle("is-selected",l===t)})}};e=()=>{if(r){r.innerHTML="";var l=o.slideNodes().length;for(let e=0;e<l;e++){var t=document.createElement("button");t.classList.add("embla__dot"),t.addEventListener("click",()=>o.scrollTo(e)),r.appendChild(t)}c()}};l&&l.addEventListener("click",()=>o.scrollPrev()),t&&t.addEventListener("click",()=>o.scrollNext()),o.on("select",c),o.on("init",e),e()})});