var searchInput=document.querySelector("#quickSearch"),listItems=document.querySelectorAll("#sidebarnav .nav-item");listItems.length&&searchInput.addEventListener("input",function(){let t=this.value.toLowerCase(),n=[];listItems.forEach(function(e){e.textContent.toLowerCase().includes(t)?(e.style.display="",n.push(e)):e.style.display="none"}),n.length?console.log(n.length+" items found"):console.log("No items found")});